import  React, { Component } from "react";
import Particles from 'react-tsparticles';

class ParticleSettings extends Component{
    render() {
        return (
            <div>
                {/* <Particles
                   height="1000px" width="100vw"
                   id="tsparticles"
                   options={{
                       background:{
                           color:{
                               value: "#0d47a1"
                           },
                       },
                       fpsLimit:60,
                       interactivity:{
                           detect_on: 'canvas',
                           events:{
                               onClick:{
                                   enable: 'true',
                                   mode: 'push'
                               },
                               onHover: {
                                   enable: 'true',
                                   mode: 'repulsive'
                               },
                               resize : 'true',
                           },
                           modes: {
                               bubble:{
                                   distance: 400,
                                   duration: 2,
                                   opacity: 0.8,
                                   size: 40,
                               },
                               push:{
                                   quantity: 4,
                               },
                               repulse:{
                                   distance: 200,
                                   duration: 0.4,
                               },
                           }
                       }
                   }} 
                /> */}
                <div id="tsparticles"></div>
                import { tsParticles } from "https://cdn.skypack.dev/tsparticles";



{ tsParticles.load("tsparticles", {
  fpsLimit: 60,
  background: {
    color: "#0b032d"
  },
  backgroundMode: {
    enable: true
  },
  particles: {
    color: {
      value: ["#f67e7d", "#843b62", "#621940"]
    },
    links: {
      color: "#ffb997",
      enable: true
    },
    move: {
      enable: true,
      speed: 6
    },
    size: {
      value: 5,
      random: {
        enable: true,
        minimumValue: 1
      },
      animation: {
        enable: true,
        speed: 2.5,
        minimumValue: 1
      }
    },
    opacity: {
      value: 0.8,
      random: {
        enable: true,
        minimumValue: 0.4
      }
    }
  }
})}

            </div>
        )
    }
}

export default ParticleSettings;
